import React from 'react';

import { Form, Input } from 'antd';

const HargaTonaseInput = ({ label, fieldName }) => {
    const fieldStyle = {
        display: 'inline-block',
        marginLeft: '8px'
    }

    return (
        <Form.Item label={label} layoutCol={{ span: 4}} wrapperCol={{ span: 8}}>
            <Form.Item name={fieldName} style={fieldStyle}>
                <Input 
                    type="number"
                    prefix="Rp"
                    />
            </Form.Item>
            <Form.Item name={fieldName + "Tonnage"} style={fieldStyle}>
                <Input 
                    type="number"
                    suffix="tons"
                    />
            </Form.Item>
        </Form.Item>
    );
};

export default HargaTonaseInput;
